-- SMI vs AFCO MTO flag doesn't match
SELECT VS.ixSKU 'SMI_SKU',
    VS.sVendorSKU 'AFCO_SKU',
    SKU.sDescription,
    (CASE when SKU.flgMadeToOrder = 1 then 'Yes'
     ELSE 'No'
     END) as 'SMI_MTO',
     AD.AFCO_MTO
FROM tblVendorSKU VS 
    left join tblVendor V on V.ixVendor = VS.ixVendor    
    join vwSKUMultiLocation SKU on SKU.ixSKU = VS.ixSKU
    left join (-- AFCO data
            select SKULL.ixSKU, SKULL.iQAV, 
                    (CASE when SKULL.flgMadeToOrder = 1 then 'Yes'
                     ELSE 'No'
                     END) 'AFCO_MTO'
            from [AFCOReporting].dbo.vwSKULocalLocation SKULL
            where flgDeletedFromSOP = 0    
            )AD on AD.ixSKU COLLATE SQL_Latin1_General_CP1_CS_AS = VS.sVendorSKU  COLLATE SQL_Latin1_General_CP1_CS_AS      
where VS.ixVendor = '0106'
    and VS.iOrdinality = 1 -- Primary Vendor
    and SKU.flgIsKit = 0
    AND (CASE when SKU.flgMadeToOrder = 1 then 'Yes'
         ELSE 'No'
         END) <> AD.AFCO_MTO -- 10,495 MATCH    364 DO NOT
group by VS.ixSKU,
    VS.sVendorSKU,
    VS.ixVendor,
    V.sName,
    V.ixBuyer,
    SKU.sDescription,
    SKU.mPriceLevel1,
